mapper.select:
select portfolio_website.product.item_id, portfolio_website.product.name, portfolio_website.product.image, portfolio_website.product.price, portfolio_website.product.sales_volume
from portfolio_website.product
where item_id = #{itemId}

mapper.update:
update portfolio_website.product
set sales_volume = #{salesVolume}
where item_id = #{itemId};
